package com.leetcode.hard;

/**
 * 
You are given a string, s, and a list of words, words, that are all of the same length. Find all starting indices of 
substring(s) in s that is a concatenation of each word in words exactly once and without any intervening characters.

Example 1:

Input:
  s = "barfoothefoobarman",
  words = ["foo","bar"]
Output: [0,9]
Explanation: Substrings starting at index 0 and 9 are "barfoor" and "foobar" respectively.
The output order does not matter, returning [9,0] is fine too.
Example 2:

Input:
  s = "wordgoodgoodgoodbestword",
  words = ["word","good","best","word"]
Output: []
 */


import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class SubstringWithConcatenationOfAllwords4 {

	public static void main(String[] args) {
		List<Integer> result = null;
		result = findSubstring("barfoothefoobarman", new String[] {"foo", "bar"});
		System.out.println(result.toString());
		result = findSubstring("wordgoodgoodgoodbestword", new String[] {"word","good","best","word"});
		System.out.println(result.toString());
		result = findSubstring("barfoofoobarthefoobarfooman", new String[] {"bar","foo","foo","the"});
		System.out.println(result.toString());
		result = findSubstring("", 
				new String[] {"ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"});
		System.out.println(result.toString());
	}
	
	public static List<Integer> findSubstring(String s, String[] words) {
		Map<String, Integer> wordFrequency = new HashMap<>();
        Map<String, Integer> map = new HashMap<>();
        List<Integer> result = new ArrayList<>();
        for(String word : words){
            wordFrequency.put(word, wordFrequency.getOrDefault(word, 0) + 1);
        }
        int wordLength =  words[0].length();
        for(int i = 0; i < wordLength; i++){
        	map.clear();
        	int wordCount = 0;
            int start = i;
            for(int j = i; j <= s.length() - wordLength; j += wordLength){
            	String subWord = s.substring(j, j + wordLength);
                if(wordFrequency.containsKey(subWord)){
                	map.put(subWord, map.getOrDefault(subWord, 0) + 1);
                    wordCount++;
                    while(map.get(subWord) > wordFrequency.get(subWord)){
                    	String newSubWord = s.substring(start, start + wordLength);
                        map.put(newSubWord, map.get(newSubWord) - 1);
                        start += wordLength;
                        wordCount--;
                    }
                    if(wordCount == words.length){
                        result.add(start);
                        String newSubWord = s.substring(start, start + wordLength);
	                    map.put(newSubWord, map.get(newSubWord) - 1);
                        wordCount--;
                        start += wordLength;
                    }
                } else {
                    wordCount = 0;
                    map.clear();
                    start = j + wordLength;
                }
            }
        }
        return result;
	}

}
